Function Invoke-MessageBox
{

    [cmdletbinding()]
    Param(
        [Parameter(Position = 0, Mandatory, HelpMessage = "Enter the text message to display.")]
        [string]$Message,
        [string]$Title = "",
        [ValidateSet("Information", "Warning", "Error", "Question", "Shield")]
        [string]$Icon = "Information",
        [ValidateSet("OK", "OKCancel", "YesNo")]
        [string]$Buttons = "OK"
    )

    Remove-Variable -Name "Return" -Scope script -ErrorAction SilentlyContinue

    Add-Type -AssemblyName PresentationFramework -ErrorAction stop
    Add-Type -assemblyName PresentationCore -ErrorAction stop
    Add-Type -AssemblyName System.Windows.Forms
    [System.Windows.Forms.Application]::EnableVisualStyles()

    $Form = New-Object System.Windows.Window

    $Form.Title = $Title
    $Form.Height = 200
    $Form.Width = 300
    $Form.WindowStartupLocation = [System.Windows.WindowStartupLocation]::CenterScreen

    $Base64ImgEncoded = @{
        "Information" = ""
        "Warning"     = ""
        "Error"       = ""
        "Question"    = ""
        "Shield"      = ""
    }

    $ObjBitmapImage = New-Object System.Windows.Media.Imaging.BitmapImage #Provides a specialized BitmapSource that is optimized for loading images using Extensible Application Markup Language (XAML).
    $ObjBitmapImage.BeginInit() #Signals the start of the BitmapImage initialization.
    $ObjBitmapImage.StreamSource = [System.IO.MemoryStream][System.Convert]::FromBase64String($Base64ImgEncoded[$Icon]) #Creates a stream whose backing store is memory.
    $ObjBitmapImage.EndInit() #Signals the end of the BitmapImage initialization.
    $ObjBitmapImage.Freeze() #Makes the current object unmodifiable and sets its IsFrozen property to true.
    
    $Grid = New-Object System.Windows.Controls.Grid

    $img = New-Object System.Windows.Controls.Image
    $img.Source = $ObjBitmapImage
    $img.Width = 50
    $img.Height = 50
    $img.HorizontalAlignment = "left"
    $img.VerticalAlignment = "top"
    $img.Margin = "15,5,0,0"

    $Grid.AddChild($img)

    $text = New-Object System.Windows.Controls.Textblock
    $text.text = $Message
    $text.Padding = 10
    $text.Width = 200
    $text.Height = 80
    $text.Margin = "75,5,0,0"
    $text.TextWrapping = [System.Windows.TextWrapping]::Wrap
    $text.VerticalAlignment = "top"

    $text.HorizontalAlignment = "left"
    $Grid.AddChild($text)

    Switch ($Buttons)
    {

        "OK"
        {
            $btn = New-Object System.Windows.Controls.Button
            $btn.Content = "_OK"
            $btn.Width = 75
            $btn.Height = 25
            $btn.Margin = "0,80,0,0"
            $btn.Add_click( {
                    $Form.close()
                    $script:Return = $true
                })
            $Grid.AddChild($btn)
            $Form.add_Loaded( { $btn.Focus() })
        } #ok
        "OKCancel"
        {
            $btnOK = New-Object System.Windows.Controls.Button
            $btnOK.Content = "_OK"
            $btnOK.Width = 75
            $btnOK.Height = 25

            $btnOK.Margin = "-75,75,0,0"
            $btnOK.Add_click( {
                    $Form.close()
                    $script:Return = $true
                })
            $btnCan = New-Object System.Windows.Controls.Button
            $btnCan.Content = "_Cancel"
            $btnCan.Width = 75
            $btnCan.Height = 25
            $btnCan.Margin = "120,75,0,0"
            $btnCan.Add_click( {
                    $Form.close()
                    $script:Return = $false
                })
            $Grid.AddChild($btnOK)
            $Grid.AddChild($btnCan)
            $Form.add_Loaded( { $btnOK.Focus() })
        } #okcancel
        "YesNo"
        {
            $btnY = New-Object System.Windows.Controls.Button
            $btnY.Content = "_Yes"
            $btnY.Width = 75
            $btnY.Height = 25
            $btnY.Margin = "-75,75,0,0"
            $btnY.Add_click( {
                    $Form.close()
                    $script:Return = $true
                })
            $btnNo = New-Object System.Windows.Controls.Button
            $btnNo.Content = "_No"
            $btnNo.Width = 75
            $btnNo.Height = 25
            $btnNo.Margin = "120,75,0,0"
            $btnNo.Add_click( {
                    $Form.close()
                    $script:Return = $false
                })
            $Grid.AddChild($btnY)
            $Grid.AddChild($btnNo)
            $Form.add_Loaded( { $btnY.Focus() })
        } #yesno
    }
    
   

    #display the form
    $Form.AddChild($Grid)
    [void]$Form.ShowDialog()

    #write the button result to the pipeline if not using -Quiet
    if (-Not $Quiet)
    {
        $script:Return
    }
}

